import React, { Component } from 'react';

class Resumo extends Component {
    
        constructor(props){
            super(props);
            this.state = {
                consultas : {
                    consultas_30dias_anteriores : 0,
                    consultas_30dias_posteriores : 0
                },
                faturamento : {
                    anterior : {
                        valor: 0,
                        comparativo : 0
                    },
                    previsao : {
                        valor: 0,
                        comparativo : 0
                    }
                }
            }
        }

        componentDidMount(){
            fetch("http://projeto.devmedia.com.br/php/api-dashboard/api/resumo").then(resultado => resultado.jason().then(dados => this.setState(dados)));

        }
        
        render(){
            return(
                <div>
                    <div className="col">
                        <h1 className=" mt-2 display-1 text-left">QUADROS</h1>
                        <h3 className="text-length mt-5">Sala Iracema</h3>
                        <div className="row">


                            <div className="col">
                                <div className="row">
                                    <div className="col">
                                        <div className="card mt-2 text-center ">
                                            <div className="card-header">
                                                Tensão
                                        </div>
                                            <div className="card-body">
                                                {this.state.consultas.consultas_30dias_anteriores}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="col">
                                <div className="card mt-2 text-center">
                                    <div className="card-header">
                                        Corrente
                                    </div>
                                    <div className="card-body">
                                        {this.state.consultas.consultas_30dias_posteriores}
                                    </div>
                                </div>
                            </div>

                        </div></div>
                    
                
            
            
                <div className="col">
                    <h3 className="mt-2">Faturamento</h3>

                        <div className="row">
                            <div className="col">
                                <div className="card mt-2 text-center">
                                    <div className="card-header">
                                        Acumulado
                                </div>
                                    <div className="card-body">
                                        {this.state.faturamento.anterior.valor.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}
                                        <span className={"badge ml-1 "}>
                                            {this.state.faturamento.anterior.comparativo} %
                                    </span>
                                    </div>
                                </div>
                            </div>
                            <div className="col">
                                <div className="card mt-2 text-center">
                                    <div className="card-header">
                                        Diário
                                </div>
                                    <div className="card-body">
                                        {this.state.faturamento.previsao.valor.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}
                                        <span className={"badge ml-1 " + (this.state.faturamento.previsao.comparativo > 0 ?
                                            "badge-success" : "badge-danger")}>
                                            {this.state.faturamento.previsao.comparativo} %
                                    </span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>                           
        )
    }
}
export default Resumo;
